public boolean equals(Object obj) {
  if (this == obj) {
    return true;
  }
  if (obj == null) {
    return false;
  }
  if (getClass() != obj.getClass()) {
    return false;
  }
  XAttr other = (XAttr) obj;
  if (name == null) {
    if (other.name != null) {
      return false;
    }
  } else if (!name.equals(other.name)) {
    return false;
  }
  if (ns != other.ns) {
    return false;
  }
  if (!Arrays.equals(value, other.value)) {
    return false;
  }
  return true;
}
