public void deserialize(DataInput in, int length, Text w) throws IOException {
  try {
    w.setCapacity(length, true);
    w.setLength(length);
  } catch (final Exception e) {
    throw new IOException(e);
  }
  final byte[] bytes = w.getBytes();
  in.readFully(bytes, 0, length);
}
