    EnumSet<AddBlockFlag> flags) {
  List<AddBlockFlagProto> ret = new ArrayList<>();
  for (AddBlockFlag flag : flags) {
    AddBlockFlagProto abfp = AddBlockFlagProto.forNumber(flag.getMode());
    if (abfp != null) {
      ret.add(abfp);
    }
  }
  return ret;
}

