void resetFlushTimer() throws IOException {
  if (flushTimerTask != null) {
    IOException exception = flushTimerTask.getException();
    flushTimerTask.stop();
    if (exception != null) {
      throw exception;
    }
    flushTimerTask = null;
  }
  isTimerActive = false;
}
