public void rampDownReduces(int rampDown) {
  //remove from the scheduled and move back to pending
  while (rampDown > 0) {
    ContainerRequest request = scheduledRequests.removeReduce();
    if (request == null) {
      return;
    }
    pendingReduces.add(request);
    rampDown--;
  }
}

