Boolean hasAccess(Job job, HttpServletRequest request) {
  UserGroupInformation callerUgi = UserGroupInformation
      .createRemoteUser(request.getRemoteUser());
  if (!job.checkAccess(callerUgi, JobACL.VIEW_JOB)) {
    return false;
  }
  return true;
}
