protected void serviceStop() throws Exception {
  if (stopped.getAndSet(true)) {
    // return if already stopped
    return;
  }
  if (eventHandlingThread != null) {
    eventHandlingThread.interrupt();
  }
  if (launcherPool != null) {
    launcherPool.shutdown();
  }
  super.serviceStop();
}
