protected Set<String> resolveHosts(String[] src) {
  Set<String> result = new HashSet<String>();
  if (src != null) {
    for (int i = 0; i < src.length; i++) {
      if (src[i] == null) {
        continue;
      } else if (isIP(src[i])) {
        result.add(resolveHost(src[i]));
      } else {
        result.add(src[i]);
      }
    }
  }
  return result;
}

