protected String[] resolveHosts(String[] src) {
  String[] result = new String[src.length];
  for (int i = 0; i < src.length; i++) {
    if (isIP(src[i])) {
      result[i] = resolveHost(src[i]);
    } else {
      result[i] = src[i];
    }
  }
  return result;
}
