public float getProgress() {
  this.readLock.lock();
  try {
    computeProgress();
    return (this.setupProgress * this.setupWeight + this.cleanupProgress
        * this.cleanupWeight + this.mapProgress * this.mapWeight + this.reduceProgress
        * this.reduceWeight);
  } finally {
    this.readLock.unlock();
  }
}
