private long getLaunchTime() {
  long launchTime = 0;
  for (TaskAttempt at : attempts.values()) {
    //select the least launch time of all attempts
    if (launchTime == 0  || launchTime > at.getLaunchTime()) {
      launchTime = at.getLaunchTime();
    }
  }
  if (launchTime == 0) {
    return this.scheduledTime;
  }
  return launchTime;
}
