private void writeStatusFile(String status) throws IOException {
  FileOutputStream fos = new FileOutputStream(new File(results,
              testName + ".pstatus"));
  BufferedWriter out = null;
  try {
    out = new BufferedWriter(new OutputStreamWriter(fos, "UTF8"));
    out.write(status + "\n");
  } finally {
    if (out != null) {
      out.close();
    } else {
      fos.close();
    }
  }
}
