private static boolean isTruthy(String str) {
  if (str == null)
    return false;
  if (str.equalsIgnoreCase(""))
    return false;
  if (str.equalsIgnoreCase("false"))
    return false;
  if (str.equalsIgnoreCase("no"))
    return false;
  if (str.equalsIgnoreCase("off"))
    return false;
  if (str.equalsIgnoreCase("disable"))
    return false;
  return true;
}
