private boolean rejectRootDirectoryDelete(boolean isEmptyDir,
    boolean recursive) throws PathIOException {
  if (isEmptyDir) {
    return true;
  }
  if (recursive) {
    return false;
  } else {
    throw new PathIOException(this.bucket, "Can not delete root path");
  }
}
