public int read() throws IOException {
  if (null == this.byteBuffer) {
    throw new IOException("this byte buffer for InputStream is null");
  }
  if (!this.byteBuffer.hasRemaining()) {
    return -1;
  }
  return this.byteBuffer.get() & 0xFF;
}
