public synchronized void reset() throws IOException {
  if (this.isClosed) {
    throw new IOException("Closed in InputStream");
  }
  try {
    this.byteBuffer.reset();
  } catch (InvalidMarkException e) {
    throw new IOException("Invalid mark");
  }
}
