public void close() throws IOException {
  if (this.isClosed) {
    return;
  }
  if (null == this.byteBuffer) {
    throw new IOException("Can not close a null object");
  }

  this.flush();
  this.byteBuffer.flip();
  this.byteBuffer = null;
  this.isFlush = false;
  this.isClosed = true;
}
