protected void serviceStop() throws Exception {
  scheduler.shutdown();
  if (!scheduler.awaitTermination(collectorLingerPeriod,
      TimeUnit.MILLISECONDS)) {
    LOG.warn(
        "Scheduler terminated before removing the application collectors");
  }
  collectorManager.stop();
  super.serviceStop();
}

