public static OpportunisticSchedulerMetrics getMetrics() {
  if(!isInitialized.get()){
    synchronized (OpportunisticSchedulerMetrics.class) {
      if(INSTANCE == null){
        INSTANCE = new OpportunisticSchedulerMetrics();
        registerMetrics();
        isInitialized.set(true);
      }
    }
  }
  return INSTANCE;
}
