public SubClusterInfo getSubCluster(final SubClusterId subClusterId)
    throws YarnException {
  if (isCachingEnabled()) {
    return getSubClusters(false).get(subClusterId);
  } else {
    return stateStore
        .getSubCluster(GetSubClusterInfoRequest.newInstance(subClusterId))
        .getSubClusterInfo();
  }
}
