public static void logAndThrowException(Logger log, String errMsg,
    Throwable t) throws YarnException {
  if (t != null) {
    log.error(errMsg, t);
    throw new YarnException(errMsg, t);
  } else {
    log.error(errMsg);
    throw new YarnException(errMsg);
  }
}
