void addLocation(String location, int count) {
  Map<String, AtomicInteger> m = rackLocations;
  if (!location.startsWith("/")) {
    m = nodeLocations;
  }
  if (count == 0) {
    m.remove(location);
  } else {
    m.put(location, new AtomicInteger(count));
  }
}
