void removeLocation(String location) {
  Map<String, AtomicInteger> m = rackLocations;
  AtomicInteger count = m.get(location);
  if (count == null) {
    m = nodeLocations;
    count = m.get(location);
  }

  if (count != null) {
    if (count.decrementAndGet() == 0) {
      m.remove(location);
    }
  }
}
