private int getTotalAllocations(
    List<Map<Resource, List<Allocation>>> allocations) {
  int totalAllocs = 0;
  for (Map<Resource, List<Allocation>> allocation : allocations) {
    for (List<Allocation> allocs : allocation.values()) {
      totalAllocs += allocs.size();
    }
  }
  return totalAllocs;
}
