public Resource getResource() {
  NodeHeartbeatResponseProtoOrBuilder p = viaProto ? proto : builder;
  if (this.resource != null) {
    return this.resource;
  }
  if (!p.hasResource()) {
    return null;
  }
  this.resource = convertFromProtoFormat(p.getResource());
  return this.resource;
}
