private static String getConfValue(String defaultValue, String keySuffix,
    Configuration conf, String... keys) {
  String value = null;
  for (String key : keys) {
    if (keySuffix != null) {
      key += "." + keySuffix;
    }
    value = conf.get(key);
    if (value != null) {
      break;
    }
  }
  if (value == null) {
    value = defaultValue;
  }
  return value;
}
