private void deleteDir(Path path) {
  try {
    LOG.info("Deleting {}", path);
    if (fs.delete(path, true)) {
      metrics.incrLogsDirsCleaned();
    } else {
      LOG.error("Unable to remove {}", path);
    }
  } catch (IOException e) {
    LOG.error("Unable to remove {}", path, e);
  }
}
