public static void logAndThrowException(String errMsg, Throwable t)
    throws YarnException {
  if (t != null) {
    LOG.error(errMsg, t);
    throw new YarnException(errMsg, t);
  } else {
    LOG.error(errMsg);
    throw new YarnException(errMsg);
  }
}
