protected void serviceStart() throws Exception {
  super.serviceStart();
  LOG.info("Scheduling HBase liveness monitor at interval {}",
      monitorInterval);
  monitorExecutorService.scheduleAtFixedRate(new HBaseMonitor(), 0,
      monitorInterval, TimeUnit.MILLISECONDS);
}
