public static int flushCompactTableRegions(HRegionServer server,
    TableName table) throws IOException {
  List<HRegion> regions = server.getRegions(table);
  for (HRegion region : regions) {
    region.flush(true);
    region.compact(true);
  }
  return regions.size();
}
