private boolean disksTurnedBad(Set<String> preCheckFailedDirs,
    Set<String> postCheckDirs) {
  boolean disksFailed = false;
  for (String dir : postCheckDirs) {
    if (!preCheckFailedDirs.contains(dir)) {
      disksFailed = true;
      break;
    }
  }
  return disksFailed;
}
