public void onClose(Session session, int status, String reason) {
  try {
    LOG.info(session.getRemoteAddress().getHostString() + " closed!");
    String exit = "exit\r\n";
    pair.out.write(exit.getBytes(Charset.forName("UTF-8")));
    pair.out.flush();
    pair.in.close();
    pair.out.close();
  } catch (IOException e) {
  } finally {
    session.close();
  }
}

