protected void closeCurrentBlockReaders() {
  resetCurStripeBuffer(false);
  if (blockReaders ==  null || blockReaders.length == 0) {
    return;
  }
  for (int i = 0; i < groupSize; i++) {
    closeReader(blockReaders[i]);
    blockReaders[i] = null;
  }
  blockEnd = -1;
}
