public final void stderr(Path stderrDir, String stdErrFile) throws IOException {
  if (!stderrDir.isAbsolute()) {
    throw new IOException("Stdout path must be absolute");
  }
  redirectStdErr = true;
  setStdErr(new Path(stderrDir, stdErrFile));
}
