public void stop() {
  sched.shutdown();
  boolean isShutdown = false;
  try {
    isShutdown = sched.awaitTermination(10, TimeUnit.SECONDS);
  } catch (InterruptedException e) {
    sched.shutdownNow();
    isShutdown = true;
  }
  if (!isShutdown) {
    sched.shutdownNow();
  }
  super.stop();
}
