public String getDeviceFileType(String devName) throws IOException {
  Shell.ShellCommandExecutor shexec = new Shell.ShellCommandExecutor(
      new String[]{"stat", "-c", "%F", devName});
  shexec.execute();
  return shexec.getOutput();
}
