public DeviceType getDeviceTypeFromDeviceNumber(int major, int minor) {
  if (shellWrapper.existFile("/sys/dev/block/"
      + major + ":" + minor)) {
    return DeviceType.BLOCK;
  }
  return DeviceType.CHAR;
}
