public Set<Device> getDevices() {
  Set<Device> devices = null;

  CommandExecutor executor =
      commandExecutorProvider.apply(new String[]{this.binaryPath});
  try {
    executor.execute();
    String output = executor.getOutput();
    devices = parseOutput(output);
  } catch (IOException e) {
    LOG.warn(e.toString());
  }
  return devices;
}
