public Set<Device> allocateDevices(Set<Device> availableDevices, int count,
    Map<String, String> env) {
  // Can consider topology, utilization.etc
  Set<Device> allocated = new HashSet<>();
  int number = 0;
  for (Device d : availableDevices) {
    allocated.add(d);
    number++;
    if (number == count) {
      break;
    }
  }
  return allocated;
}
