private String getScriptFromSearchDirs(String binaryName,
    String[] scriptPaths) {
  String scriptPath = null;

  for (String dir : scriptPaths) {
    File f = new File(dir, binaryName);
    if (f.exists()) {
      LOG.info("Found script: {}", dir);
      scriptPath = f.getAbsolutePath();
      break;
    }
  }

  return scriptPath;
}
