private char getDevType(Path p, String fromStat) {
  if (fromStat.contains("character")) {
    return 'c';
  } else if (fromStat.contains("block")) {
    return 'b';
  } else {
    throw new IllegalArgumentException(
        "File is neither a char nor block device: " + p);
  }
}
