public void basicSchedule(Set<Device> allocation, int count,
    Set<Device> availableDevices) {
  // Basic scheduling
  // allocate all available
  if (count == availableDevices.size()) {
    allocation.addAll(availableDevices);
    return;
  }
  int number = 0;
  for (Device d : availableDevices) {
    allocation.add(d);
    number++;
    if (number == count) {
      break;
    }
  }
}
