private boolean initializeWhenGpuRequested(Container container)
    throws ContainerExecutionException {
  if (!requestsGpu(container)) {
    return false;
  }

  // Do lazy initialization of gpu-docker plugin
  if (additionalCommands == null) {
    init();
  }

  return true;
}
