  if (!finished) {
    this.finished = true;
    if (timer != null) {
      timer.cancel();
      timer = null;
    }
    if (!unregisterWithoutDelay) {
      scheduleTimerTaskForUnregistration();
    } else {
      ContainerMetrics.unregisterContainerMetrics(ContainerMetrics.this);
    }
    this.pMemMBQuantiles.stop();
    this.cpuCoreUsagePercentQuantiles.stop();
  }
}

