  if (json == null) {
    return null;
  }

  final Map<?, ?> m = (Map<?, ?>) json.get(AclStatus.class.getSimpleName());

  AclStatus.Builder aclStatusBuilder = new AclStatus.Builder();
  aclStatusBuilder.owner((String) m.get("owner"));
