public static boolean isStoppable(DockerContainerStatus containerStatus) {
  if (containerStatus.equals(DockerContainerStatus.RUNNING)
      || containerStatus.equals(DockerContainerStatus.RESTARTING)) {
    return true;
  }
  return false;
}
