private LinuxContainerRuntime pickPluggableRuntime(
    Map<String, String> environment) {
  for (LinuxContainerRuntime runtime : pluggableRuntimes) {
    if (runtime.isRuntimeRequested(environment)) {
      return runtime;
    }
  }
  return null;
