private boolean isPluggableRuntime(String runtimeType) {
  for (LinuxContainerRuntimeConstants.RuntimeType type :
      LinuxContainerRuntimeConstants.RuntimeType.values()) {
    if (type.name().equalsIgnoreCase(runtimeType)) {
      return false;
    }
  }
  return true;
}
