public synchronized int available() throws IOException{
  getInputStream();
  if(fileLength != null){
    long remaining = fileLength - currentPos;
    return remaining <= Integer.MAX_VALUE ? (int) remaining : Integer.MAX_VALUE;
  }else {
    return Integer.MAX_VALUE;
  }
}
