public static PrivilegedOperationExecutor getInstance(Configuration conf) {
  if (instance == null) {
    synchronized (PrivilegedOperationExecutor.class) {
      if (instance == null) {
        instance = new PrivilegedOperationExecutor(conf);
      }
    }
  }

  return instance;
}
